import { Webhook } from "standardwebhooks";
import { headers } from "next/headers";
import { getDodoPaymentsClient } from "@/lib/dodopayments";

const webhook = new Webhook(process.env.DODO_PAYMENTS_WEBHOOK_KEY!);

export async function POST(request: Request) {
  const headersList =  await headers();
  try {
    const rawBody = await request.text();
    const webhookHeaders = {
      "webhook-id": headersList.get("webhook-id") || "",
      "webhook-signature": headersList.get("webhook-signature") || "",
      "webhook-timestamp": headersList.get("webhook-timestamp") || "",
    };
    await webhook.verify(rawBody, webhookHeaders);
    const payload = JSON.parse(rawBody);

    if (payload.data.payload_type === "Subscription") {
      switch (payload.type) {
        case "subscription.active":
          const subscription = await getDodoPaymentsClient().subscriptions.retrieve(payload.data.subscription_id);
          console.log("-------SUBSCRIPTION DATA START ---------")
          console.log(subscription)
          console.log("-------SUBSCRIPTION DATA END ---------")
          break;
        case "subscription.failed":
          break;
        case "subscription.cancelled":
          break;
        case "subscription.renewed":
          break;
        case "subscription.on_hold":
          break
        default:
          break;
      }
    } else if (payload.data.payload_type === "Payment") {
        switch (payload.type) {
            case "payment.succeeded":
              const paymentDataResp = await getDodoPaymentsClient().payments.retrieve(payload.data.payment_id)
              console.log("-------PAYMENT DATA START ---------")
              console.log(paymentDataResp)
              console.log("-------PAYMENT DATA END ---------")
              break;
            default:
                break;
        }
    }
    return Response.json(
      { message: "Webhook processed successfully" },
      { status: 200 }
    );
  } catch (error) {
    console.log(" ----- webhook verification failed -----")
    console.log(error)
    return Response.json(
      { message: "Webhook verification failed" },
      { status: 400 }
    );
  }
}

import { z } from "zod";
import {NextRequest, NextResponse} from "next/server";
import { getStripe } from "@/lib/stripe";


const productCartItemSchema = z.object({
    name: z.string().min(1),
    quantity: z.number().int().min(1),
    amount: z.number().int().min(1),
});

const stripe = getStripe();

const customerSchema = z.object({
    email: z.string().email(),
    name: z.string().min(1)
});

const checkoutSessionSchema = z.object({
    productCart: z.array(productCartItemSchema).min(1),
    customer: customerSchema,
    return_url: z.string().url,
    metadata: z.record(z.string(), z.string()).optional()

})


export async function POST(req: NextRequest) {
    try{
        const body = await req.json();
        const validation = checkoutSessionSchema.safeParse(body);

        if(!validation.success) {
            return NextResponse.json({
                error: "Validation failed",
                details: validation.error.issues.map(issue => ({
                    field: issue.path.join("."),
                    message: issue.message
                }))

            }
            , {status: 400}
            )
        }

        const {productCart, customer, return_url, metadata} = validation.data;

        const session = await stripe?.checkout.sessions.create({
            payment_method_types: ["card"],
            line_items: productCart.map(item => ({
                price_data: {
                    currency: "usd",
                    product_data: {name: item.name},
                    unit_amount: item.amount
                },
                quantity: item.quantity
            })),
            mode: "payment",
            customer_email: customer.email,
            success_url: `${return_url}?status=success&session_id={CHECKOUT_SESSION_ID}`,
            cancel_url: `${return_url}?status=cancelled`,
            metadata
        });
        return NextResponse.json({url: session.url})
    } catch (error) {
        console.error("Stripe checkout error: ", error);
        return NextResponse.json({error: "Internal server error"}, {status: 500})

    }

}

